/**
 * @author suchendra
 * @description whether passed string is null or not.
 * @name check-null-string
 */


function checkNullString(input) {
    // Returns true if the input is either undefined, null, or empty, false otherwise
    return (input === undefined || input === null || input === '');
}

// Make the main function available to other packages that require us
module.exports = checkNullString;